mapper.select:
select

from loadbalance_server
where is_delete = 0
and lb_mark = #{lbMark}
and srv_ip = #{ip}
and srv_port = #{port}

mapper.update:
update loadbalance_server
set
  srv_status = #{srvStatus}
where is_delete = 0
and id = #{id,jdbcType=BIGINT}

update loadbalance_server
set
  srv_enable = #{srvStatus}
where is_delete = 0
and id = #{id,jdbcType=BIGINT}
